(function () {
    var n = document.getElementsByClassName("txtwav"); 
    console.log(n);
    for (var e = 0, t = n.length; e < t; e++) {
        var a = n[e], r = a.textContent.trim(); 
        a.innerHTML = null;
        i(a, r)
    }

    function i(di, elem) {
        for (var t in elem) {
            var sp = document.createElement("span");
            sp.innerHTML = elem[t] === " " ? "&nbsp;" : elem[t];
            di.appendChild(sp)
        }
    }
})();